CREATE TABLE MEMBRE (
	id int(4) auto_increment primary key,
	nom varchar( 255 )  not null,
	fonction varchar( 255 )  not null,
	institution varchar(255) not null,
	telephone varchar(30) ,
	email varchar(50));

CREATE TABLE CHERCHEUR (
	id int(4) auto_increment primary key ,
	nom varchar(50) not null ,
	prenom varchar(50) not null ,
	id_centre int(4) ,
	sexe varchar (1) ,
	lieu_naissance varchar (200) ,
	date_naissance date ,
	titre_academique varchar (25) ,
	date_engagement date ,
	anciennete int (5) ,
	grade varchar (50) ,
    matricule varchar (15) ,
	inventions text , 
	champs_activites text ,
    contribution_service_public text ,
    societes_savantes text ,
    activites_scientifiques text ,
    publications text ,
	email varchar (255) ,
	telephone varchar (50) ,
	adresse text ,
	photo varchar(50));
	-- photo : le nom "nom.jpg" ou "nom.png" de la photo

CREATE TABLE ATTESTATION (
	id int not null auto_increment primary key, 
	nom_attestation varchar (50) ,
	date_impression date ,
	num_attestation varchar(50),
	signature varchar(50) ,
	id_chercheur int);

CREATE TABLE CENTRE (                    
	id int(4) auto_increment primary key, 
	nom_centre varchar(100) not null,    
	niveau varchar(100) not null,
	adresse_contacts varchar(255) ,
	ville varchar(100) ,
	province varchar(100)); 
	
	ALTER TABLE CHERCHEUR ADD constraint fk10 foreign key(id_centre) 
	references CENTRE(id);
	-- ON DELETE CASCADE : si on supprime un CHERCHEUR toutes ces données
	-- dans la table CHERCHEUR seront supprimées

-- 1 	
	ALTER TABLE ATTESTATION ADD constraint foreign key(id_chercheur) 
	references CHERCHEUR(id) ON DELETE CASCADE;

	DESCRIBE CENTRE;
	DESCRIBE MEMBRE;
	DESCRIBE CHERCHEUR;
	DESCRIBE ATTESTATION;
	DESCRIBE UTILISATEUR;

